import './App.css';
import {useState} from "react";

function App() {

    const[name,setName]=useState('');
    const[dateTime, setDateTime] = useState('');
    const[description, setDescription] = useState('');

    function addNewTransaction(e) {
        e.preventDefault();
        const url = process.env.REACT_APP_APU_URL + '/transaction';
        // console.log(url);
        fetch(url, {
            method: 'POST',
            headers: {'Content-type' : 'application/json'},
            body: JSON.stringify({
                name,dateTime,description
            })
        }).then(response=>{
            response.json().then(json => {
                console.log('result',json);
            })
        });
    }

  return (
    <main>
      <h1>$4000<span>.00</span></h1>
      <form onSubmit={addNewTransaction}>
          <div className='basic'>
              <input type='text'
                     value={name}
                     onChange={(e)=>setName(e.target.value)}
                     placeholder={'+200 for new Phone'}/>
              <input value={dateTime}
                     onChange={(e) => setDateTime(e.target.value)}
                     type='datetime-local'/>
          </div>
          <div className='description'>
              <input value={description}
                     onChange={(e)=>setDescription(e.target.value)}
                     type='text' placeholder={'description'}/>
          </div>
          <button type='submit'>Add new Transaction</button>
      </form>
        <div className='transactions'>
            <div className='transaction'>
                <div className='left'>
                    <div className='name'>New Phone</div>
                    <div className='description'>Time for new phone</div>
                </div>
                <div className='right'>
                    <div className='price red'>-$500</div>
                    <div className='datetime'>2023-05-04 15:45</div>
                </div>
            </div>
            <hr />
            <div className='transaction'>
                <div className='left'>
                    <div className='name'>Job</div>
                    <div className='description'>Time for new phone</div>
                </div>
                <div className='right'>
                    <div className='price green'>+$700</div>
                    <div className='datetime'>2023-05-04 15:45</div>
                </div>
            </div>
            <hr />
            <div className='transaction'>
                <div className='left'>
                    <div className='name'>New Jacket</div>
                    <div className='description'>Time for new phone</div>
                </div>
                <div className='right'>
                    <div className='price red'>-$350</div>
                    <div className='datetime'>2023-05-04 15:45</div>
                </div>
            </div>
        </div>
    </main>
  );
}

export default App;
